mapper.select:
SELECT *
  FROM CHALLENGE_VALIDATION
 WHERE CHAL_NO = #{chalNo}
   AND USER_ID = #{userId}
 ORDER BY NO DESC

SELECT *
  FROM CHALLENGE_VALIDATION
 WHERE NO = #{no}
   AND USER_ID = #{userId}
   AND TRUNC(VLD_DATE) = TO_DATE(#{vldDate},'YYYYMMDD')

SELECT * FROM( SELECT rownum rn, a.* from(
SELECT no,title,deduct_point,chal_start_date,chal_end_date,
CASE WHEN chal_start_date <=trunc(current_date) AND chal_end_date <= trunc(current_date) THEN '완료' WHEN TRUNC(chal_start_date) <= TRUNC(current_date) And trunc(current_date) <= chal_end_date Then '진행중' Else '진행전' END as chstatus
FROM CHALLENGES
order by no
)a where rownum <= #{last}) b where rn >= #{first}
