mapper.update:
		update EXPERIENCE_GROUP
        set NOW_APLYT = NOW_APLYT + 1
        where NO = #{no}

mapper.select:
SELECT * FROM( SELECT rownum rn, a.* from(
SELECT no,title,subject,lmt_aplyt,ness_grd,expe_start_date,expe_end_date,
CASE WHEN expe_start_date <=trunc(current_date) AND expe_end_date <= trunc(current_date) THEN '완료' WHEN TRUNC(expe_start_date) <= TRUNC(current_date) And trunc(current_date) <= expe_end_date Then '진행중' Else '진행전' END as exstatus
FROM EXPERIENCE_GROUP
order by no
)a where rownum <= #{last}) b where rn >= #{first}

mapper.update:
UPDATE EXPERIENCE_GROUP
TITLE = #{title}, SUBJECT = #{subject}, LMT_APLYT = #{lmtAplyt}, NESS_GRD = #{nessGrd}, EXPE_START_DATE = #{expeStartDate}, REVIEW_END_DATE = #{reviewEndDate}, THUMBNAIL_IMG = #{thumbnailImg}, EXPE_END_DATE = #{expeEndDate}, REVIEW_START_DATE = #{reviewStartDate}, THUMBNAIL_IMG_PATH = #{thumbnailImgPath}
where no = #{no}
