mapper.select:
SELECT U.USER_ID, U.USER_NAME, U.NICK_NAME, U.PROFILE_IMG_PATH, M.*, GM.*, GL.*
FROM USERS U JOIN MEALS M ON (U.USER_ID = M.USER_ID) JOIN GROUP_MEMBER GM ON (M.USER_ID = GM.USER_ID) JOIN GROUP_LISTS GL ON (GM.NO = GL.NO)
WHERE TRUNC(post_date) = TRUNC(#{postDate}) AND M.USER_ID = #{userId}

mapper.delete:
DELETE FROM GROUP_MEMBER
WHERE NO = #{grpNo} AND USER_ID
= #{userId}
