mapper.insert:
SELECT MAX (NO)+1 FROM MEALS
insert into Meals( no, user_id, subject, img, file_dir, meal_score, type_code, meal_time, meal_amount, post_date
)values ( #{no}, #{userId}, #{subject}, #{img}, #{fileDir}, #{mealScore}, #{typeCode}, #{mealTime}, #{mealAmount}, sysdate
)

mapper.select:
SELECT *
FROM MEALS
WHERE TRUNC(post_date) = TO_DATE(#{postDate},'yyyyMMdd') AND user_id = #{userId}

mapper.update:
UPDATE MEALS
user_id = #{userId}, subject = #{subject}, img = #{img}, file_dir = #{fileDir}, meal_score = #{mealScore}, type_code = #{typeCode}, meal_time = #{mealTime}, meal_amount = #{mealAmount}, post_date = sysdate
WHERE NO = #{no}

mapper.select:
		SELECT  round(NVL(AVG(meal_score) ,0),1) "meal_Avg"
  		FROM meals
 		 where user_id = #{userId}
