mapper.insert:
SELECT MAX (NO)+1 FROM MEALS
insert into Meals( no, user_id, subject, img, file_dir, meal_score, type_code, meal_time, meal_amount, post_date
)values ( #{no}, #{userId}, #{subject}, #{img}, #{fileDir}, #{mealScore}, #{typeCode}, #{mealTime}, #{mealAmount}, sysdate
)

mapper.select:
SELECT *
FROM MEALS
WHERE TRUNC(post_date) = TO_DATE(#{postDate},'yyyyMMdd') AND user_id = #{userId}
